mapper.sql:
WHERE i.is_deleted = 'N'

    AND i.name LIKE CONCAT('%', #{influencerName}, '%')



    AND EXISTS (
    SELECT 1
    FROM hashtag_influencer_campaign h_cat
    WHERE h_cat.influencer_id = i.influencer_id
    AND h_cat.category_id IN

        #{id}

    )



    AND y.subscriber >= #{minSubscriber}


    AND y.subscriber <= #{maxSubscriber}


    AND ig.follower >= #{minFollower}


    AND ig.follower <= #{maxFollower}


    AND i.price >= #{minPrice}


    AND i.price <= #{maxPrice}

WHERE i_sub.is_deleted = 'N'

    AND i_sub.name LIKE CONCAT('%', #{influencerName}, '%')



    AND EXISTS (
    SELECT 1
    FROM hashtag_influencer_campaign h_cat_sub
    WHERE h_cat_sub.influencer_id = i_sub.influencer_id
    AND h_cat_sub.category_id IN

        #{id}

    )



    AND y_sub.subscriber >= #{minSubscriber}


    AND y_sub.subscriber <= #{maxSubscriber}


    AND ig_sub.follower >= #{minFollower}


    AND ig_sub.follower <= #{maxFollower}


    AND i_sub.price >= #{minPrice}


    AND i_sub.price <= #{maxPrice}
