mapper.select:
SELECT COUNT(DISTINCT p.pipeline_id) FROM pipeline p JOIN campaign c ON p.campaign_id = c.campaign_id JOIN client_company cc ON c.client_company_id = cc.client_company_id LEFT JOIN pipeline_influencer_client_manager picm ON p.pipeline_id = picm.pipeline_id AND picm.client_manager_id IS NOT NULL LEFT JOIN client_manager cm ON cm.client_manager_id = picm.client_manager_id LEFT JOIN pipeline_user pu ON pu.pipeline_id = p.pipeline_id LEFT JOIN user u ON pu.user_id = u.user_id JOIN pipeline_status ps ON p.pipeline_status_id = ps.pipeline_status_id WHERE p.pipeline_step_id = #{pipelineStepId} AND p.is_deleted = 'N'
AND p.name LIKE CONCAT('%', #{req.keyword}, '%')
AND cc.client_company_name LIKE CONCAT('%', #{req.keyword}, '%')
AND cm.name LIKE CONCAT('%', #{req.keyword}, '%')
AND u.user_id = #{req.userId}
AND p.pipeline_status_id = #{req.filter}

SELECT DISTINCT category_id
FROM hashtag_influencer_campaign
WHERE campaign_id = #{campaignId}

SELECT c.campaign_id AS campaignId, c.campaign_name AS campaignName, cs.campaign_status_id AS campaignStatusId, cs.status_name AS campaignStatusName, c.client_company_id, cc.client_company_name AS clientCompanyName, cm.name AS clientManagerName, cm.position AS clientManagerPosition, c.product_name AS productName, p.expected_revenue AS expectedRevenue, DATE_FORMAT(p.started_at, '%Y-%m-%d') AS startedAt, DATE_FORMAT(p.ended_at, '%Y-%m-%d') AS endedAt, u.name AS pipelineUserName, u.position AS pipelineUserPosition FROM campaign c JOIN campaign_status cs ON c.campaign_status_id = cs.campaign_status_id JOIN client_company cc ON c.client_company_id = cc.client_company_id JOIN client_manager cm ON cm.client_company_id = cc.client_company_id AND cm.is_deleted = 'N' JOIN pipeline p ON p.campaign_id = c.campaign_id JOIN client_company_user pu ON pu.client_company_id = cc.client_company_id JOIN user u ON pu.user_id = u.user_id AND u.is_deleted = 'N'
AND cs.campaign_status_id = #{request.campaignStatus}
AND p.expected_revenue <= 10000000
AND p.expected_revenue >= 10000001 AND p.expected_revenue <= 30000000
AND p.expected_revenue >= 30000001 AND p.expected_revenue <= 50000000
AND p.expected_revenue >= 50000001 AND p.expected_revenue <= 70000000
AND p.expected_revenue >= 70000001 AND p.expected_revenue <= 100000000
AND p.expected_revenue >= 100000001
AND p.started_at >= #{request.startedDate}
AND p.ended_at <= #{request.endedDate}
AND cc.client_company_name LIKE CONCAT('%', #{request.clientCompanyName}, '%')
AND cm.name LIKE CONCAT('%', #{request.clientManagerName}, '%')
GROUP BY c.campaign_id ORDER BY p.created_at DESC, c.campaign_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT p.campaign_id, ps.step_name AS stepType, DATE_FORMAT(p.started_at, '%Y-%m-%d') AS startedAt FROM pipeline p JOIN pipeline_step ps ON p.pipeline_step_id = ps.pipeline_step_id WHERE p.campaign_id IN
#{id}
AND ps.step_name = #{request.stepType}
