mapper.select:
SELECT a.satisfaction_id , a.email_status , a.is_reacted , a.response_date , a.sent_date , b.campaign_name , c.youtube_link , c.instagram_link , d.name as clientManagerName , d.email , e.client_company_name , f.name as userName , g.name as influencerName FROM satisfaction a JOIN campaign b ON a.campaign_id = b.campaign_id JOIN pipeline p ON p.campaign_id = b.campaign_id JOIN pipeline_influencer_client_manager c ON c.pipeline_id = p.pipeline_id JOIN client_manager d ON d.client_manager_id = c.client_manager_id JOIN client_company e ON e.client_company_id = d.client_company_id JOIN user f ON f.user_id = p.writer_id JOIN influencer g ON g.influencer_id = c.influencer_id WHERE p.pipeline_status_id = 6
AND title LIKE CONCAT('%', #{ title }, '%')
LIMIT #{ limit } OFFSET #{ offset };

SELECT COUNT(*) FROM satisfaction
AND title LIKE CONCAT('%', #{ title }, '%')
LIMIT #{ limit } OFFSET #{ offset };
